    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String op= (String) acomp.getSelectedItem();
    int precioacom, total, asientos, precioclase;
    String clase;
    
    asientos= (int)cantSpin.getValue();
    if (Vip.isSelected()) {
        precioclase= 100;
    } else {
        precioclase= 80;
    }
    
    if (op.equals("Palomitas")){
        precioacom= 30;
    }
    else if (op.equals("Nachos")) {
        precioacom= 35;
    } 
    else {
        precioacom=20;
    }
    
    total= (precioclase+precioacom)*asientos;
    totalPre.setValue(total);
// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

